// Uploading files
var file_frame;jQuery(".aesop-generator-attr-image_upload").live("click",function(e){e.preventDefault();if(file_frame){file_frame.open();return}file_frame=wp.media.frames.file_frame=wp.media({title:jQuery(this).data("uploader_title"),button:{text:jQuery(this).data("uploader_button_text")},multiple:!1});file_frame.on("select",function(){attachment=file_frame.state().get("selection").first().toJSON();jQuery("#aesop-generator-attr-img").val(attachment.url)});file_frame.open()});